//go:build e2etests
// +build e2etests

package authentication_test

import (
	"testing"

	"github.com/dapr/components-contrib/pubsub"
	"github.com/dapr/components-contrib/pubsub/jetstream"
	"github.com/dapr/kit/logger"
)

func TestAuthentication(t *testing.T) {
	testCases := []struct {
		desc        string
		jwt         string
		seedKey     string
		expectError bool
	}{
		{
			desc:        "Valid jwt and valid seed",
			jwt:         "eyJ0eXAiOiJKV1QiLCJhbGciOiJlZDI1NTE5LW5rZXkifQ..7K5VA23V8bBipH4Vkzhg01_jaQVw6MWW9NgxUkiYBUaMLNHLZB4_DnuT2mBz9SrQwi8XohNoZeKI7WTTbQxxBg",
			seedKey:     "SUAJF7ALJKFPLKTA23BOP6CILVPH3SV4V7EGM7VFZ3KCFWHKPPI7HCCZKI",
			expectError: false,
		},
		{
			desc:        "Valid jwt and invalid seed",
			jwt:         "eyJ0eXAiOiJKV1QiLCJhbGciOiJlZDI1NTE5LW5rZXkifQ..7K5VA23V8bBipH4Vkzhg01_jaQVw6MWW9NgxUkiYBUaMLNHLZB4_DnuT2mBz9SrQwi8XohNoZeKI7WTTbQxxBg",
			seedKey:     "SUAIGUGTLK7FZMRVYLGCROG3TTCZK64D2MZOYFGKH7WJRADFYOMTL3QQZU",
			expectError: true,
		},
		{
			desc:        "Invalid jwt and valid seed",
			jwt:         "eyJ0eXAiOiJKV1QiLCJhbGciOiJlZDI1NTE5LW5rZXkifQ..Cj_dQFE9c_BDZrHLzIJRtDI0ZrXB1u-eG_NJw54Dg3Df3QdJOrl-rU_M6f0D0rAGH1THQl0vD4lRkGp_SfzeAQ",
			seedKey:     "SUAJF7ALJKFPLKTA23BOP6CILVPH3SV4V7EGM7VFZ3KCFWHKPPI7HCCZKI",
			expectError: true,
		},
	}
	for _, tC := range testCases {
		t.Run(tC.desc, func(t *testing.T) {
			log := logger.NewLogger("natsE2E")
			js := jetstream.NewJetStream(log)
			md := pubsub.Metadata{
				Properties: map[string]string{
					"natsURL":        "nats://localhost",
					"jwt":            tC.jwt,
					"seedKey":        tC.seedKey,
					"durableName":    "test",
					"queueGroupName": "test",
					"startSequence":  "0",
					"startTime":      "0",
					"deliverAll":     "true",
					"flowControl":    "false",
				},
			}
			err := js.Init(md)
			if err != nil && !tC.expectError {
				t.Fatal("Did not expect error during connect", err.Error())
			}
			if err == nil && tC.expectError {
				t.Fatal("Did expect error during connect")
			}
		})
	}
}
